INSERT INTO JOB_DESCRIPTION (job_reference_number, html_page) VALUES ('QWE12', '<section>\n      <h1>Java Developer</h1>\n      <section>\n        <h2>Job Overview</h2>\n        <p>The position is anchored in <em>JAVASTORM Digital Product Department</em>, responsible for delivering the\n          best\n          value in software applications for our internal and external clients.\n          As a Java Developer, you will be responsible for designing, developing, testing, and maintaining Java-based\n          applications according to the agreed enterprise architecture.\n          You will work closely with a team of developers and project managers to deliver high-quality software as\n          solutions to business problems in partnership with external Product owners.\n          You will also be responsible for troubleshooting and debugging issues, as well as staying up-to-date with\n          emerging trends and technologies in Java development.\n        </p>\n        <p>With most large organizations using Java to implement software systems and backend services, a Java developer\n          is one of the most sought-after jobs today. </p>\n        <!--References: https://www.simplilearn.com/java-developer-job-description-article-->\n        <p>The Java Developer reports to <strong>Development Team Leader</strong> and serves in the specialist team.</p>\n      </section>\n      <div class=\"job_position_details\">\n        <p>\n          <a href=\"about.php\">JavaStorm</a>\n          &middot; Melbourne, VIC 3000 (On-site)\n        </p>\n        <p><img class=\"icon\" src=\"images/salary_icon.png\" alt=\"salary icon\"> $70, 000 - $85, 000 a year</p>\n        <p><img class=\"icon\" src=\"images/jobtype_icon.png\" alt=\"jobtype icon\"> Full-Time &middot; Contract</p>\n        <p><img class=\"icon\" src=\"images/employee_icon.png\" alt=\"employee icon\"> 500 - 800 employees &middot;\n          Staffing and Recruiting\n        </p>\n        <p><img class=\"icon\" src=\"images/shift_icon.png\" alt=\"shift icon\"> 8 hour shift &middot; Monday to Friday</p>\n        <p><img class=\"icon\" src=\"images/recruit_icon.png\" alt=\"recruit icon\"> Actively Recruiting</p>\n      </div>\n    </section>\n\n    <section class=\"job--responsibilities\">\n      <h2>Key Responsibilities</h2>\n      <ol>\n        <li>Working with business and technology stakeholders to understand and analyse requirements for new features\n          and how they will impact the existing application.</li>\n        <li>Develop flowcharts, layouts and documentation to identify requirements and solutions.</li>\n        <li>Design and implement Java applications with well-written code that runs efficiently and optimally.</li>\n        <li>Contribute to software verifications plans and quality assurance procedures.</li>\n        <li>Comply with project plans, industry standards and regulatory requirements.</li>\n        <li>Knowledge sharing and conduct code reviews of other team members.</li>\n        <li>Stay up-to-date with emerging trends and technologies in Java development.</li>\n      </ol>\n      <!--References: https://www.linkedin.com/jobs/search/?currentJobId=3493983784&geoId=106808692&keywords=java%20developer-->\n    </section>\n\n    <aside class=\"job--benefits\">\n      <h2>Benefits</h2>\n      <ol>\n        <li>A competitive salary with annual performance-based bonus and stock option schemes. </li>\n        <li>A pension plan; an employer funded health and medical plan; life insurance plan; long term disability\n          coverage; paid time off; an employee assistance program; and a discount platform.</li>\n        <li>Provide a technology stipend or allowance to cover the cost of purchasing or upgrading equipment and\n          software.</li>\n      </ol>\n      <br>\n      <p>\n        <strong>Notes: </strong>The availability and specifics of these benefits vary by position, details of which\n        will be provided during the hiring process.\n      </p>\n    </aside>\n    <section class=\"job--skills\">\n      <h2>Skills and Qualifications</h2>\n      <h3>Essential</h3>\n      <ul>\n        <li>Advanced skills in Java 11 or later.</li>\n        <li>Experience with popular Java frameworks such as Spring, Hibernate, and Struts.</li>\n        <li>Familiarity with software development tools such as Eclipse, IntelliJ IDEA, and Git.</li>\n        <li>Sound knowledge of Object-Oriented Programming (OOP) Patterns and Concepts</li>\n        <li>Basic knowledge of Web Technologies like HTML, JavaScript, CSS, JQuery</li>\n        <li>Excellent problem-solving and analytical skills.</li>\n        <li>Strong communication and collaboration skills.</li>\n        <li>High sense of initiative to work without a close supervision.</li>\n      </ul>\n      <h3>Preferable</h3>\n      <ul>\n        <li>Familiarity with Agile development methodologies.</li>\n        <li>Bachelor&#39;s Degree or Master&#39;s degree in Computer Science, Information Technology, or a related\n          field.\n        </li>\n        <li>Certification in Java programming (at least 3 years).</li>\n        <li>Experience with cloud computing platforms such as Amazon Web Services (AWS) or Microsoft Azure.</li>\n        <li>Basic Understanding of the concepts of MVC (Model-View-Controller) Pattern, JDBC (Java Database\n          Connectivity).</li>\n      </ul>\n      <!--References: https://www.simplilearn.com/java-developer-job-description-article-->\n    </section>');

INSERT INTO JOB_DESCRIPTION (job_reference_number, html_page) VALUES ('DEF67', ' <section>\n      <h1>Security Analyst</h1>\n      <section>\n        <h2>Job Overview</h2>\n        <p>\n          This role is part of the\n          <em>JavaStorm Digital Security Department</em> and specializes in\n          software solution security initiatives across the group. In other\n          words, it is the focal point for all security-related concerns from\n          different aspects, including the network and data of the software\n          application. The incumbent will have to maintain the\n          confidentiality, integrity, and availability of the technology\n          infrastructure in the developed software. The role extends to\n          identifying and mitigating potential security risks and\n          vulnerabilities in the software solution before any breaches occur.\n          This role closely collaborates with the developer team, quality\n          assurance team, and project managers to ensure the security of the\n          solutions while ensuring they comply with industry standards and\n          regulations. It is also accountable for conducting security\n          assessments and providing recommendations to improve the systems\n          security posture.\n        </p>\n        <!--\n          References: https://au.indeed.com/viewjob?jk=ea03543999903ba7&q=security+analyst&tk=1gtnf02d8jb6o801&from=web&advn=5850178102896955&adid=393283413&ad=-6NYlbfkN0ANnJH4QnLFBfULq3qOrbXdSo6KiiJAwgPJGbTQogKUodBu7Fv551Nz7sATA_feM3S6WB_lH7lLdWxtWgoX_kvyEmVTwCdSEHgQOXsAq03SsdCH---a3qikAA8wsYwFpG_cNUIWWJMGxdGO8zpKEws_VxGVtu7Iff6YqVtM3h2rCHPFuJGZ4CKzseAPK26fJNyBB35wcfgitBnIrUkjmrHA07Ydohq2k1OmpiZOBL4_jDMkBEgVrJ7NSWame8s_QhD6iPrRFt_TNyw1BOylcs14CrpoDxJRj5Xp5Vo8d2t1yI_EPc1pclpoRjoaV2-BIZeNe41N9QWi4YisvP6Vm3XjqXBgavfAkyKtskDWh8RawOrEW0i9hm28Cdq-_YzH_GSIfmT6bVlJoE8GpKL9egh6DnKL837iwoUdBE8a3r4vd98kTMysD1q1St8ZpbTeOgA%3D&sjdu=vUz3PJIdpGfygLQXVlxEDtrp31zrUKvX5DyE7UoHSnYlvOGmGBiSV4pcJuWlkSmqhVEOLPGXccge6tB02tluZaxTsTJbkzSrccqG5nG1SEDXGwmNWsCt8xBLpXHoXAf3&acatk=1gtnl7p85k2ip800&pub=4a1b367933fd867b19b072952f68dceb&xkcb=SoCP-_M3R3WPi8WT3B0LbzkdCdPP&vjs=3\n        -->\n        <p>\n          The Security Analyst reports to the\n          <strong>Project Manager</strong> throughout the software development\n          lifecycle.\n        </p>\n      </section>\n      <div class=\"job--description__details\">\n        <p>\n          <a href=\"about.php\">JavaStorm</a>\n          &middot; Melbourne, VIC 3000 (On-site/Remote)\n        </p>\n        <p>\n          <img class=\"icon\" src=\"images/salary_icon.png\" alt=\"salary icon\"> $130, 000 -\n          $145, 000 a year\n        </p>\n        <p>\n          <img class=\"icon\" src=\"images/jobtype_icon.png\" alt=\"jobtype icon\"> Full-Time\n          &middot; Contract\n        </p>\n        <p>\n          <img class=\"icon\" src=\"images/employee_icon.png\" alt=\"employee icon\"> 500 - 800\n          employees &middot; Staffing and Recruiting\n        </p>\n        <p>\n          <img class=\"icon\" src=\"images/shift_icon.png\" alt=\"shift icon\"> Flexible working\n          hours &middot; Monday to Friday\n        </p>\n        <p>\n          <img class=\"icon\" src=\"images/recruit_icon.png\" alt=\"recruit icon\"> Actively\n          Recruiting\n        </p>\n      </div>\n    </section>\n<section class=\"job--responsibilities\">\n      <h2>Key Responsibilities</h2>\n      <ol>\n        <li>\n          Conduct security assessments of the software applications and\n          infrastructure to identify and address potential security risks and\n          vulnerabilities.\n        </li>\n        <li>\n          Perform triage, in-depth investigation, and remediation to all\n          detected security incidents and breaches in a timely fashion.\n        </li>\n        <li>\n          Ensure the software solutions are secured from unauthorized access\n          or activity while maintaining the relevant data.\n        </li>\n        <li>\n          Capture and document security incidents, findings, and responses in\n          an organized manner.\n        </li>\n        <li>\n          Work with developers and project managers to implement security best\n          practices throughout the software development processes.\n        </li>\n        <li>\n          Stay up-to-date with emerging trends and technologies in information\n          security and incorporate those into the software applications.\n        </li>\n        <li>\n          Ensure the software solutions comply with industry standards and\n          regulations, such as PCI-DSS, HIPAA, and GDPR.\n        </li>\n      </ol>\n      <!--References: https://au.indeed.com/viewjob?jk=df212eb686dc09f7&from=tp-serp&tk=1gto398g9jb6o800-->\n    </section>\n\n    <aside class=\"job--benefits\">\n      <h2>Benefits</h2>\n      <ol>\n        <li>\n          A competitive salary with annual performance-based bonus and stock\n          option schemes.\n        </li>\n        <li>\n          A comprehensive benefits package includes health insurance, dental\n          and vision insurance, retirement plans, and other perks such as paid\n          time off, wellness programs, and employee discounts.\n        </li>\n        <li>\n          Opportunities include conferences, workshops, or online courses for\n          professional development and training.\n        </li>\n        <li>\n          Flexible work arrangements, such as remote work or flexible\n          schedules, for a better work-life balance.\n        </li>\n      </ol>\n      <p>\n        <strong>Notes: </strong>The availability and specifics of these\n        benefits vary by position, details of which will be provided during\n        the hiring process.\n      </p>\n    </aside>\n\n    <section class=\"job--skills\">\n      <h2>Skills and Qualifications</h2>\n      <section>\n        <h3>Essential</h3>\n        <ul>\n          <li>\n            Bachelor&#39;s degree in Computer Science, Information Technology,\n            Computer Engineering or equivalent.\n          </li>\n          <li>\n            At least three years of experience in information security,\n            preferably in a software development environment.\n          </li>\n          <li>\n            Intermediate coding skill in Python, PowerShell, or Jenkins is\n            required.\n          </li>\n          <li>\n            Strong understanding of information security concepts and\n            practices, such as cryptography, network security, and application\n            security.\n          </li>\n          <li>\n            Experience with security testing tools and techniques, such as\n            vulnerability scanning and penetration testing.\n          </li>\n          <li>\n            Familiarity with software development tools such as Git and\n            Jenkins.\n          </li>\n          <li>\n            High-energy work ethic, with the ability to establish a vision,\n            drive change, and deliver results.\n          </li>\n          <li>Good anticipation, analytical, and problem-solving skills.</li>\n          <li>\n            Excellent verbal and written communication skills, especially in\n            English, interpersonal, and teaching skills.\n          </li>\n        </ul>\n      </section>\n      <section>\n        <h3>Preferable</h3>\n        <ul>\n          <li>\n            Master&#39;s Degree in Information Security or Cybersecurity.\n          </li>\n          <li>\n            At least two years of experience with threat-hunting, analysis,\n            and triage of cyber security threats, including exposure to\n            incident response, vulnerability management, and attack testing\n            and validation.\n          </li>\n          <li>\n            CISSP, CISA, CISM, or other industry-recognized security\n            certification(s).\n          </li>\n          <li>\n            Basic knowledge of cloud computing platforms such as Amazon Web\n            Services (AWS) or Microsoft Azure.\n          </li>\n          <li>Familiarity with Agile development methodologies.</li>\n        </ul>\n        <!--References: https://www.linkedin.com/jobs/search/?currentJobId=3558978542&geoId=106808692&keywords=security%20analyst&location=Malaysia&refresh=true-->\n      </section>\n    </section>');


